import React, { useEffect, useState } from "react";
import * as Yup from "yup";
import { useFormik } from "formik";
import { toast } from "react-toastify";
import { Navigate } from "react-router-dom";
import ProductApi from "../../../api/ProductAPI";
const ProductCreate = () => {
  const [loading, setLoading] = useState(false);
  const [objSubmitData, setObjSubmitData] = useState("");
  const formik = useFormik({
    initialValues: {
      name: "",
      price: "",
      priceSale: "",
      avarta: "",
      brandID: "",
      shortDes: "",
      fullDes: "",
      status: "",
      amount: "",
    },
    validationSchema: Yup.object({
      userName: Yup.string().required("Vui l√≤ng nh·∫≠p Username"),
      // .test(
      //   "T√†i kho·∫£ng ch∆∞a ƒë∆∞·ª£c s·ª≠ dung",
      //   "T√†i kho·∫£ng ƒë√£ t·ªìn t·∫°i ",
      //   function (userName) {
      //     console.log(
      //       "üöÄ ~ file: RegisterTab.js ~ line 45 ~ RegisterTab ~ userName",
      //       userName
      //     );

      //     console.log(isUserName);
      //     return isUserName;
      //   }
      // ),
      name: Yup.string()
        .max(200, "T√™n s·∫£n ph·∫©m kh√¥ng qu√° 200 k√≠ t·ª±")
        .min(4, "T√™n s·∫£n ph·∫©m ng·∫Øn nh·∫•t 4 k√≠ t·ª± ")
        .required("vui l√≤ng nh·∫≠p t√™n s·∫£n ph·∫©m"),
      price: "",
      priceSale: "",
      avarta: "",
      brandID: "",
      shortDes: "",
      fullDes: "",
      status: "",
      amount: "",
      fristName: Yup.string()
        .min(2, "H·ªç v√† t√™n ƒë·ªám √≠t nh·∫•t 2 k√≠ t·ª±")
        .max(25, "H·ªç v√† t√™n kh√¥ng qu√° 25 k√≠ t·ª±")
        .required("Vui l√≤ng nh·∫≠p H·ªç v√† t√™n ƒë·ªám"),

      confirmPassword: Yup.string()
        .oneOf([Yup.ref("password")], "Password kh√¥ng kh·ªõp")
        .required("Vui l√≤ng nh·∫≠p X√°c nh·∫≠n m·∫≠t kh·∫©u "),
    }),
    onSubmit: (valuesForm) => {
      setObjSubmitData({
        fristName: valuesForm.fristName,
        lastName: valuesForm.lastName,
        userName: valuesForm.userName,
        password: valuesForm.password,
        email: valuesForm.email,
      });
      formik.resetForm({
        fristName: "",
        lastName: "",
        userName: "",
        email: "",
        password: "",
        confirmPassword: "",
      });

      // setEmail(valuesForm.email);
      // setPassWord(valuesForm.password);
    },
  });
  useEffect(() => {
    async function fechData() {
      if (objSubmitData.length !== 0) {
        setLoading(true);
        const reposeData = await ProductApi.getTopNewProduct(objSubmitData);
        if (reposeData.status === 201) {
          toast.success("ƒêƒÉng k√Ω th√†nh c√¥ng", {
            className: "top-10",
            position: toast.POSITION.TOP_RIGHT,
            autoClose: 3000,
          });

          setLoading(false);
          Navigate("/login");
        } else if (reposeData.status >= 400 && reposeData < 500) {
          toast.error("ƒêƒÉng k√Ω th·∫•t b·∫°i", {
            className: "top-10",
            position: toast.POSITION.TOP_RIGHT,
            autoClose: 3000,
          });
          setLoading(false);
        } else if (reposeData.status >= 500) {
          toast.warning("C√≥ s·ª± c·ªë x·∫£y ra ch√∫ng t√¥i r·∫•t ti·∫øc v√¨ ƒëi·ªÅu n√†y", {
            className: "top-10",
            position: toast.POSITION.TOP_RIGHT,
            autoClose: 3000,
          });
          setLoading(false);
        }
      }
    }
    fechData().catch(() => {
      setLoading(false);
      toast.error("ƒêƒÉng k√Ω th·∫•t b·∫°i", {
        className: "top-10",
        position: toast.POSITION.TOP_RIGHT,
        autoClose: 3000,
      });
      // toast.error(`ƒêƒÉng k√Ω th·∫•t b·∫°i`, {
      //   position: toast.POSITION.TOP_RIGHT,
      //   autoClose: 3000,
      // });
    });
  }, [objSubmitData]);
  return (
    <div>
      <h3 className="text-3xl text-blue-600 font-medium pb-4">Th√™m s·∫£n ph·∫©m</h3>
      <div className={` h-100%  top-0 left-0 right-0  `}>
        <div className={`block p-6 rounded-lg shadow-lg bg-white  `}>
          <form onSubmit={formik.handleSubmit}>
            <div className="form-group mb-3">
              <label
                htmlFor="fristName"
                className="form-label inline-block mb-2 text-gray-700"
              >
                H·ªç v√† t√™n ƒë·ªám
              </label>
              <input
                type="text"
                className="form-control block  w-full px-3 py-1.5 text-base font-normal text-gray-700 bg-white bg-clip-padding
                border border-solid border-gray-300 rounded transition ease-in-out m-0 focus:text-gray-700 focus:bg-white focus:border-blue-600 focus:outline-none"
                id="fristName"
                aria-describedby="emailHelp"
                placeholder="H·ªç v√† t√™n ƒë·ªám"
                {...formik.getFieldProps("fristName")}
              />
              {formik.touched.fristName && formik.errors.fristName ? (
                <div className="text-sm text-red-500">
                  {formik.errors.fristName}
                </div>
              ) : (
                ""
              )}
            </div>
            <div className="form-group mb-3">
              <label
                htmlFor="lastName"
                className="form-label inline-block mb-2 text-gray-700"
              >
                T√™n
              </label>
              <input
                type="text"
                className="form-control block  w-full px-3 py-1.5 text-base font-normal text-gray-700 bg-white bg-clip-padding
