import React, { useEffect, useState } from "react";
import * as Yup from "yup";
import { useFormik } from "formik";
import { toast } from "react-toastify";
import { Navigate } from "react-router-dom";
import ProductApi from "../../../api/ProductAPI";
import NumberFormat from "react-number-format";
const ProductCreate = () => {
  const [loading, setLoading] = useState(false);
  const [objSubmitData, setObjSubmitData] = useState("");

  const formik = useFormik({
    initialValues: {
      name: "",
      price: "",
      priceSale: "",
      avatar: "",

      brandID: "",
      shortDes: "",
      fullDes: "",
      status: "",
      amount: "",
    },
    validationSchema: Yup.object({
      name: Yup.string()
        .max(200, "T√™n s·∫£n ph·∫©m kh√¥ng qu√° 200 k√≠ t·ª±")
        .min(4, "T√™n s·∫£n ph·∫©m ng·∫Øn nh·∫•t 4 k√≠ t·ª± ")
        .required("vui l√≤ng nh·∫≠p t√™n s·∫£n ph·∫©m"),
      price: Yup.number()
        .min(1000, "Gi√° th·∫•p nh·∫•t l√† 1000")
        .required("vui l√≤ng nh·∫≠p s·ªë ti·ªÅn"),
      priceSale: Yup.number()
        .max(Yup.ref("price"), "gi√° gi·∫£m nh·ªè h∆°n gi√° b√°n")
        .min(0, "Gi√° th·∫•p nh·∫•t l√† 0")
        .required("vui l√≤ng nh·∫≠p s·ªë ti·ªÅn"),
      avatar: Yup.object({
        featured_image: Yup.mixed().when("featured_image", {
          is: (value) => value?.length,
          then: (schema) =>
            schema
              .test("name", "Image is required", (value) => {
                return value != undefined && value[0] && value[0].name !== "";
              })
              .test("fileSize", "File must be less than 2MB", (value) => {
                return (
                  value != undefined && value[0] && value[0].size <= 2000000
                );
              })
              .test("type", "Only images are supported", (value) => {
                return (
                  value != undefined &&
                  value[0] &&
                  value[0].type.includes("image")
                );
              }),
          otherwise: (schema) => schema.nullable(),
        }),
      })
        .shape(
          {
            //your scheme here
          },
          //cyclic dependencies
          [["featured_image", "featured_image"]]
        )
        .nullable(),
      // .required("vui l√≤ng ch·ªçn h√¨nh ·∫£nh"),
      brandID: "",
      shortDes: "",
      fullDes: "",
      status: "",
      amount: "",
    }),

    onSubmit: (valuesForm) => {
      console.log("vo ƒë√¢y");
      console.log(
        "üöÄ ~ file: ProductCreate.js ~ line 56 ~ ProductCreate ~ valuesForm",
        valuesForm
      );

      setObjSubmitData({
        name: valuesForm.name,
        price: valuesForm.price,
        priceSale: valuesForm.priceSale,
        avatar: valuesForm.avatar,
      });
      formik.resetForm({
        name: "",
        price: "",
        priceSale: "",
        avarta: "",
        brandID: "",
        shortDes: "",
        fullDes: "",
        status: "",
        amount: "",
      });

      // setEmail(valuesForm.email);
      // setPassWord(valuesForm.password);
    },
  });
  useEffect(() => {
    async function fechData() {
      if (objSubmitData.length !== 0) {
        setLoading(true);
        const reposeData = await ProductApi.getTopNewProduct(objSubmitData);
        if (reposeData.status === 201) {
          toast.success("ƒêƒÉng k√Ω th√†nh c√¥ng", {
            className: "top-10",
            position: toast.POSITION.TOP_RIGHT,
            autoClose: 3000,
          });

          setLoading(false);
          Navigate("/login");
        } else if (reposeData.status >= 400 && reposeData < 500) {
          toast.error("ƒêƒÉng k√Ω th·∫•t b·∫°i", {
            className: "top-10",
            position: toast.POSITION.TOP_RIGHT,
            autoClose: 3000,
          });
          setLoading(false);
        } else if (reposeData.status >= 500) {
          toast.warning("C√≥ s·ª± c·ªë x·∫£y ra ch√∫ng t√¥i r·∫•t ti·∫øc v√¨ ƒëi·ªÅu n√†y", {
            className: "top-10",
            position: toast.POSITION.TOP_RIGHT,
            autoClose: 3000,
          });
          setLoading(false);
        }
      }
    }
    fechData().catch(() => {
      setLoading(false);
      toast.error("ƒêƒÉng k√Ω th·∫•t b·∫°i", {
        className: "top-10",
        position: toast.POSITION.TOP_RIGHT,
        autoClose: 3000,
      });
      // toast.error(`ƒêƒÉng k√Ω th·∫•t b·∫°i`, {
      //   position: toast.POSITION.TOP_RIGHT,
      //   autoClose: 3000,
      // });
    });
  }, [objSubmitData]);

  return (
    <div>
      <h3 className="text-3xl text-blue-600 font-medium pb-4">Th√™m s·∫£n ph·∫©m</h3>
      <div className={` h-100%  top-0 left-0 right-0  `}>
        <div className={`block p-6 rounded-lg shadow-lg bg-white  `}>
          <form onSubmit={formik.handleSubmit}>
            <div className="form-group mb-3">
              <label
                htmlFor="name"
                className="form-label inline-block mb-2 text-gray-700"
              >
                T√™n s·∫£n ph·∫©m
              </label>
              <input
                type="text"
                className="form-control block  w-full px-3 py-1.5 text-base font-normal text-gray-700 bg-white bg-clip-padding
                border border-solid border-gray-300 rounded transition ease-in-out m-0 focus:text-gray-700 focus:bg-white focus:border-blue-600 focus:outline-none"
                id="name"
                aria-describedby="emailHelp"
                placeholder="Nh·∫≠p t√™n s·∫£n ph·∫©m"
                {...formik.getFieldProps("name")}
              />
              {formik.touched.name && formik.errors.name ? (
                <div className="text-sm text-red-500">{formik.errors.name}</div>
              ) : (
                ""
              )}
            </div>
            <div className="form-group mb-3">
              <label
                htmlFor="price"
                className="form-label inline-block mb-2 text-gray-700"
              >
                Gi√° b√°n
              </label>
              <input
                type="number"
                className="form-control block  w-full px-3 py-1.5 text-base font-normal text-gray-700 bg-white bg-clip-padding
                border border-solid border-gray-300 rounded transition ease-in-out m-0 focus:text-gray-700 focus:bg-white focus:border-blue-600 focus:outline-none"
                id="price"
                placeholder="Nh·∫≠p gi√° b√°n "
                {...formik.getFieldProps("price")}
              />
              {formik.touched.price && formik.errors.price ? (
                <div className="text-sm text-red-500">
                  {formik.errors.price}
                </div>
              ) : (
                ""
              )}
            </div>
            <div className="form-group mb-3">
              <label
                htmlFor="priceSale"
                className="form-label inline-block mb-2 text-gray-700"
              >
                Gi√° gi·∫£m
              </label>
              <input
                type="number"
                className="form-control block  w-full px-3 py-1.5 text-base font-normal text-gray-700 bg-white bg-clip-padding
                border border-solid border-gray-300 rounded transition ease-in-out m-0 focus:text-gray-700 focus:bg-white focus:border-blue-600 focus:outline-none"
                id="priceSale"
                aria-describedby="emailHelp"
                placeholder="Nh·∫≠p gi√° b√°n "
                {...formik.getFieldProps("priceSale")}
              />
              {formik.touched.priceSale && formik.errors.priceSale ? (
                <div className="text-sm text-red-500">
                  {formik.errors.priceSale}
                </div>
              ) : (
                ""
              )}
            </div>
            <div className="form-group mb-3">
              <label
                htmlFor="avatar"
                className="form-label inline-block mb-2 text-gray-700"
              >
                Gi√° gi·∫£m
              </label>
              <input
                type="file"
                className="form-control block  w-full px-3 py-1.5 text-base font-normal text-gray-700 bg-white bg-clip-padding
                border border-solid border-gray-300 rounded transition ease-in-out m-0 focus:text-gray-700 focus:bg-white focus:border-blue-600 focus:outline-none"
                id="avatar"
                aria-describedby="emailHelp"
                placeholder="Nh·∫≠p gi√° b√°n "
                accept="image/png, image/jpeg"
                onChange={(event) => {
                  formik.setFieldValue("avatar", event.currentTarget.files[0]);
                }}
